package com.ecust.demo.mvc.action;import com.ecust.demo.service.IDemoService;import com.ecust.mvcframework.annotation.YZAutowired;import com.ecust.mvcframework.annotation.YZController;import com.ecust.mvcframework.annotation.YZRequestMapping;import com.ecust.mvcframework.annotation.YZRequestParam;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * @Author:zhenyuan * @Date:Created in 15:40 2018/12/21 */@YZController@YZRequestMapping("/demo")public class DemoAction {    @YZAutowired    private IDemoService demoService;    @YZRequestMapping("/query")    public void query(HttpServletRequest req, HttpServletResponse res,@YZRequestParam("name") String name){        String result = demoService.get(name);        try {            res.getWriter().write(result);        } catch (IOException e) {            e.printStackTrace();        }    }    @YZRequestMapping("/add")    public void add(HttpServletRequest req, HttpServletResponse res,                    @YZRequestParam("a") Integer a,@YZRequestParam("b") Integer b){        try {            res.getWriter().write(a+"+"+b+"="+(a+b));        } catch (IOException e) {            e.printStackTrace();        }    }}